sp_widget.client_script:
api.controller=function(spUtil,$scope) {
  /* widget controller */
  var c = this;
		spUtil.recordWatch($scope, "x_1125801_servicen_portfolio_project", "", function(response) {
    // Returns the data inserted or updated on the table 
    	c.server.update();   

    });
		spUtil.recordWatch($scope, "x_1125801_servicen_portfolio_category", "", function(response) {
    // Returns the data inserted or updated on the table 
    	c.server.update();   

    });
};

