sp_widget.script:
(function() {
    data.category = [];
    var gr = new GlideAggregate('x_1125801_servicen_portfolio_category');
    gr.addQuery('table', 'project');
    gr.addQuery('active', true);
    gr.groupBy('category');
    gr.query();
    while (gr.next()) {
        data.category.push(gr.category.getDisplayValue());
    }

    data.categoryRecords = [];

    function fetchRecords(categoryQuery) {
        var records = [];
        var grP = new GlideRecord('x_1125801_servicen_portfolio_project');
        grP.addQuery('active', 'true');
        grP.addEncodedQuery(categoryQuery);
        grP.orderBy('order');
        grP.query();
        while (grP.next()) {
            var url = grP.github_url ? grP.github_url.toString() : "portfolio";
            var obj = {
                'title': grP.name.getDisplayValue(),
                'image': grP.image.getDisplayValue(),
                'giturl': url

            };
				//	gs.addInfoMessage(obj.giturl);
            records.push(obj);
        }
        return records;
    }

    for (var i = 0; i < data.category.length; i++) {
        var categoryQuery = (data.category[i] === "All") ? '' : 'category.category=' + data.category[i];
        var records = fetchRecords(categoryQuery);
        var record = {
            'category': data.category[i],
            'id': data.category[i].replace(/\s/g, '-'),
            'records': records
        };
        data.categoryRecords.push(record);
    }
})();
