sp_widget.client_script:
api.controller=function(spUtil,$scope) {
  /* widget controller */
  var c = this;
	//console.log(c.data.list);

	spUtil.recordWatch($scope, "x_1125801_servicen_u_resume_education_exp", "", function(response) {
    // Returns the data inserted or updated on the table 
    	c.server.update();   

    });
};

