sp_widget.client_script:
api.controller=function(spUtil,$scope) {
  /* widget controller */
  var c = this;
	spUtil.recordWatch($scope, "sys_properties", "", function(response) {
    // Returns the data inserted or updated on the table 
    	c.server.update();   

    });

};

