sp_widget.script:
(function() {
    /* populate the 'data' object */
    /* e.g., data.table = $sp.getValue('table'); */
    data.list = [];

    var gr = new GlideRecord('sys_properties');
    gr.addEncodedQuery("name=x_1125801_servicen.Bio/About Me^ORname=x_1125801_servicen.Name^ORname=x_1125801_servicen.Portfolio Title^ORname=x_1125801_servicen.Profile Picture^ORname=x_1125801_servicen.GitHub URL^ORname=x_1125801_servicen.Instagram URL^ORname=x_1125801_servicen.Linkedin URL^ORname=x_1125801_servicen.Email Address");
    gr.query();
  var obj = {
            'name': 'Your Name',
            'bio': 'Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industrys standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum',
		        'profilepic':'atf-cloud-icon.png',
		        'title':'Enter the quote you want',
		        'email': 'portfolio',
            'git': 'portfolio',
            'linkedin': 'portfolio',
            'insta': 'portfolio'
        };
    while (gr.next()) {


        var valueName = gr.getValue('name').toString();
        var value = gr.getValue('value');
        if (valueName === 'x_1125801_servicen.Bio/About Me') {
            obj.bio = value ||  obj.bio;
        } else if (valueName === 'x_1125801_servicen.Name') {
            obj.name = value ||  obj.name;
        } else if (valueName === 'x_1125801_servicen.Portfolio Title') {
            obj.title = value ||  obj.title;
        }else if (valueName === 'x_1125801_servicen.Profile Picture') {
            obj.profilepic = value || obj.profilepic;
        } 
 if (valueName === 'x_1125801_servicen.GitHub URL') {
            obj.git = value || obj.git;
        } else if (valueName === 'x_1125801_servicen.Instagram URL') {
            obj.insta = value ||  obj.insta;
        } else if (valueName === 'x_1125801_servicen.Email Address') {
            obj.email = "mailto:"+value || obj.email ;
        } else if (valueName === 'x_1125801_servicen.Linkedin URL') {
            obj.linkedin = value || obj.linkedin;
        }

    }
    data.list.push(obj);
//gs.addErrorMessage(JSON.stringify(obj));
})();
