sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	//frontend

	data.frontEndList=[];
	var obj={};
	var grF=new GlideRecord('x_1125801_servicen_tech_skills');
	grF.addQuery('skill_category','front_end');
	grF.addQuery('active','true');
	grF.orderBy('order');
	grF.query();

	while(grF.next()){

		obj={
			'title':grF.getValue('skill_title'),
			'image':grF.getDisplayValue('icon'),

		}
		data.frontEndList.push(obj);

	}
	//backend
		data.backEndList=[];
	var obj2={};
	var grB=new GlideRecord('x_1125801_servicen_tech_skills');
	grB.addQuery('skill_category','back_end');
	grB.addQuery('active','true');
	grB.orderBy('order');
	grB.query();

	while(grB.next()){

		obj2={
			'title':grB.getValue('skill_title'),
			'image':grB.getDisplayValue('icon')

		}
		data.backEndList.push(obj2);

	}

	//android
		data.androidList=[];
	var obj3={};
	var grA=new GlideRecord('x_1125801_servicen_tech_skills');
	grA.addQuery('skill_category','android');
	grA.addQuery('active','true');
	grA.orderBy('order');
	grA.query();

	while(grA.next()){

		obj3={
			'title':grA.getValue('skill_title'),
			'image':grA.getDisplayValue('icon')

		}
		data.androidList.push(obj3);

	}

		//other
		data.otherList=[];
	var obj4={};
	var grO=new GlideRecord('x_1125801_servicen_tech_skills');
	grO.addQuery('skill_category','others');
	grO.addQuery('active','true');
	grO.orderBy('order');
	grO.query();

	while(grO.next()){

		obj4={
			'title':grO.getValue('skill_title'),
			'image':grO.getDisplayValue('icon')

		}
		data.otherList.push(obj4);

	}
})();
