sp_widget.script:
(function() {
    data.category = [];
    var grC = new GlideRecord('x_1125801_servicen_portfolio_category');
    grC.addQuery('table', 'skills');
    grC.addQuery('active', 'true');
    grC.orderBy('order');
    grC.query();

    while (grC.next()) {
        data.category.push(grC.getValue('category'));
    }

    data.categoryRecords = [];

    for (var i = 0; i < data.category.length; i++) {
        var records = [];
        var grF = new GlideRecord('x_1125801_servicen_tech_skills');
        grF.addQuery('skills_category.category', data.category[i].toString());
        grF.addQuery('active', 'true');
        grF.orderBy('order');
        grF.query();

        while (grF.next()) {
            var obj = {
                'title': grF.getValue('skill_title'),
                'image': grF.getDisplayValue('image')
            };
            records.push(obj);
        }

        var record = {
            'category': data.category[i].toString(),
            'records': records
        };

        data.categoryRecords.push(record);
    }
})();
