sp_widget.client_script:
api.controller=
function($scope, spUtil, $element, $window) {


	$scope.goTop = function() {
		var animationDuration = 300;
		if($window.scrollY > 0) {
			$('html').animate({
				scrollTop : 0
			}, animationDuration);
		} else {
			spUtil.scrollTo('.sp-scroll', animationDuration);
		}
	}

}

sp_widget.css:
a{

 text-decoration:none; 

}
