sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	data.list=[];
	var obj={};
	var gr=new GlideRecord('x_1125801_servicen_u_resume_education_exp');
	gr.addQuery('category','experience');
	gr.addQuery('active','true');
	gr.orderBy('order');
	gr.query();

	while(gr.next()){

		obj={
			'title':gr.getValue('title'),
			'date':gr.getValue('date'),
			'description':gr.getValue('description'),
			'company':gr.getValue('company'),
			'location':gr.getValue('location'),
				'job_type':gr.job_type.getDisplayValue()
		}
		data.list.push(obj);

	}



})();
