sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.list=[];
	var obj={};
	var gr=new GlideRecord('sys_properties');

		gr.addEncodedQuery("nameLIKEresume^sys_scope=bb95605493110210dd973aaefaba10ef");
	gr.query();

	while(gr.next()){
if(gr.getValue('value')==null){
		obj={
			'value':'portfolio'

		}
}else{
	obj={
			'value':gr.getValue('value')

		}

}



		data.list.push(obj);

	}
})();
