sp_widget.client_script:
api.controller = function($scope, spUtil) {
    var c = this;
    c.data = {
        education: false,
        experience: false,
        certification: false,
        tech_skills: false
    };

    function updateSwitch(name, value) {
        c.data[name] = value;
        applyCheckboxStyle(name);
    }

    function applyCheckboxStyle(name) {
        var label = document.querySelector('label[for="' + name + '"]');
        if (label) {
            label.classList.toggle('label-primary', c.data[name]);
        }
    }

    function updateServerData() {
        var switchNames = ['education', 'experience', 'certification', 'tech_skills'];
        switchNames.forEach(function(name) {
            var switchElement = document.getElementById("someSwitchOption" + name.charAt(0).toUpperCase() + name.slice(1));
            if (switchElement) {
                c.data[name] = switchElement.checked;
                applyCheckboxStyle(name);
            }
        });
        // Perform server update
        c.server.update().then(function(response) {
            // Refresh the data from the server after update
            spUtil.update($scope).then(function(newData) {
                switchNames.forEach(function(name) {
                    updateSwitch(name, newData[name]);
                });
            });
        });
    }

    c.privacy = function() {
        updateServerData();
    };

    spUtil.recordWatch($scope, "sp_widget", "filter", function(serverData) {
        updateServerData();
    });

    // Initial update of the switch when the page loads
    updateServerData();
};

