sp_widget.script:
(function() {
    data.education = false;
    data.experience = false;
	  data.certification=false;
	  data.tech_skills=false;
function updateWidgetData(widgetName, inputDataKey, dataKey) {
    var gr = new GlideRecord('sp_widget');
    gr.addQuery('name', widgetName);
    gr.query();

    if (gr.next()) {
        data[dataKey] = gr.public == true;
    }

    if (input && input[inputDataKey] !== undefined) {
        gr.public = input[inputDataKey];
        gr.update();
        data[dataKey] = gr.public == true;
    }
}

updateWidgetData('Education', 'education', 'education');
updateWidgetData('Experience', 'experience', 'experience');
updateWidgetData('Certification', 'certification', 'certification');
updateWidgetData('Tech Skills', 'tech_skills', 'tech_skills');


})();
