sp_widget.script:
(function() {
  // Initialize data object
  data.list= [];
  var count = new GlideAggregate('x_1125801_servicen_certification');
  count.addQuery('active', true);
  count.query();
  var recordCount = count.getRowCount();

  var gr = new GlideRecord('x_1125801_servicen_certification');
  gr.addQuery('active', true);
  gr.orderBy('order');
  gr.query();

  var counter = 0;

    data.slide=[];

  while (gr.next()) {
    var obj = {
      'name': gr.getValue('name'),
      'link': gr.getValue('certification_link')
    };
    data.slide.push(obj);
    ++counter;

    if (counter === 3 || counter === recordCount) {
      data.list.push(data.slide); // Pushing the whole slide array
      data.slide = []; // Resetting slide
      counter = 0;
    }
  }

  // If there are remaining slides
  if (data.slide.length > 0) {
    data.list.push(data.slide);
  }
})();
