// Define the GlideRecord object for the target table
var gr = new GlideRecord("target_table_name");

// Optionally, add a query to filter records you want to update
// gr.addQuery("field_name", "value");

// Query the records
gr.query();

// Loop through the records
while (gr.next()) {
    // Update each record
    gr.field1 = "new_value"; // Update field1 with a new value
    gr.field2 = "another_value"; // Update field2 with another value
    // Set other fields as needed

    // Save the changes to the record
    gr.update();
}
