// Define GlideRecord for the target table
var gr = new GlideRecord("target_table_name");

// Optionally, add a query to filter records (if needed)
// gr.addQuery("field_name", "value");

// Query the records
gr.query();

// Loop through the records
while (gr.next()) {
    // Initialize a new record in the target table
    var newRecord = new GlideRecord("destination_table_name");
    newRecord.initialize();

    // Set values for the new record
    newRecord.field1 = gr.field1;
    newRecord.field2 = gr.field2;
    // Set other fields as needed

    // Insert the new record into the destination table
    newRecord.insert();
}
